document.addEventListener("DOMContentLoaded", () => {
//variables

const cartBtn = document.querySelector('.cart-btn');
const closeCartBtn = document.querySelector('.close-cart');
const clearCartBtn = document.querySelector('.clear-cart');
const cartDOM = document.querySelector('.cart');
const cartOverlay = document.querySelector('.clear-overlay');
const cartItems = document.querySelector('.clear-items');
const cartTotal = document.querySelector('.clear-total');
const cartContent = document.querySelector('.cart-content');
const productsDOM = document.querySelector('.products-center');
// cart
let cart = [];

// getting the products
class Products{
async getProducts(){
    try {
        let result = await fetch('products.json');
        let data = await result.json();
        let products = Array.from(data.items);
        products = products.map(item =>{
        const {title,price} = item.fields;
        const {id} = item.sys;
        const image = item.fields.image.fields.file.url;
        return {title,price,id,image};
        });
        return products;
    } catch (error) {
        console.log(error);
    }
  }
}
// display products (UI CLASS)
class UI{
    displayProducts(products){
        //   CLG = CONSOLE.LOG
        let result = "";
        products.forEach(product => {
            result += `
             single product
            <article class="product">
               <div class="img-container">
                  <img 
                   src="${product.image}"
                   alt="product" 
                   class="product-img"
                   /> 
                  <button class="bag-btn" data-id=${product.id}>
                      <i class="fas fa-shopping-cart"></i>
                      add to bag
                  </button>
               </div>
                <h3>${product.title}</h3>
                <h4>$${product.price}</h4>
            </article>
             `;
        });
        productsDOM.innerHTML = result;
    }   
}
//local storage
 class Storage {
  static saveProducts (products){
      localStorage.setItem("products",JSON.stringify(products));
  }
 }

 
 document.addEventListener("DOMContentLoaded",() =>{
    const ui = new UI();
    const products = new Products(); 

    //get all products
    products.getProducts().then(products => {
    ui.displayProducts (products);
    Storage.saveProducts(products);
  });
 });
});

