function Increment() {
  ++h1.innerHTML;
}

function Decrement() {
  --h1.innerHTML;
}

function setNill() {
  h1.innerHTML = 0;
}

plus.addEventListener("click", () => {
  Increment();
});

minus.addEventListener("click", () => {
  Decrement();
});

reset.addEventListener("click", () => {
  setNill();
});

