def binary_search_recursive(lista, search_val, dex_mod=0):

    midle_index = (len(lista)-1) // 2
    midle_el = lista[midle_index]

    while len(lista) > 0:

        if midle_el == search_val:
            return midle_index

        if midle_el < search_val:
            binary_search_recursive(lista[midle_index+1:], search_val, dex_mod+midle_index+1)

        elif midle_el > search_val:
            binary_search_recursive(lista[:midle_index-1], search_val, dex_mod)

    return print("Did't find anything")

