def conquer(left, right):
    arr = []

    if left is None:
        return right
    if right is None:
        return left

    while left and right:
        if left[0] <= right[0]:
            arr.append(left[0])
            left = left[1:]
        else:
            arr.append(right[0])
            right = right[1:]

    arr += left
    arr += right

    return arr

