#include<iostream>
#include<iomanip>
#include<cstring>

using namespace std;
class BaseClass
{
	int privateMember;
	protected:
	int protectedMember;
	public:
	int publicMember;
};

	class PublicChild : public BaseClass{
		void show(){
			//privateMember = 1;	private members cannot be accessed from outside the class
			protectedMember = 1;	//protected members inherited as public can be accessed from derived class
			publicMember = 1;	   //public members inherited as public can be accessed from derived class
		}
	};

	class ProtectedChild : protected BaseClass{
		void show(){
			//privateMember = 1;	private members cannot be accessed from outside the class
			protectedMember = 1;	//protected members inherited as protected can be accessed from derived class
			publicMember = 1;	   //public members inherited as protected can be accessed from derived class
		}
	};

	class PrivateChild : private BaseClass{
		void show(){
			//privateMember = 1;	private members cannot be accessed from outside the class
			protectedMember = 1;	//protected members inherited as private can be accessed from derived class, but will behave private in derived class
			publicMember = 1;	   //public members inherited as private can be accessed from derived class, but will behave private in derived class
		}
	};

		class PrivateGrandChild : private PrivateChild
		{
			void show()
