<!DOCTYPE html>
<html>
<body> 

<h2>Hi!<br />
Let's play a game! <br />
Think of a number,<br />
A range of numbers,<br />
and I will try guessing it.<br /></h2>

Your number: <input type="number" id="inpt" value=""> 
A range between 1-<input type="number" id="maxidi" value=""> 
<button type="button" onclick=play()>  Enter </button>

<p id="guess"></p>
<p id="return"></p>

<script>
function play() 
{  
    var maxdistance = document.getElementById("maxidi").value;
    var max = maxdistance;
    var answer = document.getElementById("inpt").value;
    var guess = max/2; // Math.round(max/2); // guess = 500 if max is 1000
     var results = [];
    var steps = 1;
    results.push(guess); 

  for (i = 0; i < maxdistance; i++) 
  {
       if(guess==answer)
      { 
        //document.getElementById("return").innerHTML = "Found it! it's: "+guess+"!";
            results.push(' Found it in ' + steps + " guesses!");
            break;            
      }
    else if(guess > answer)
      {
          results.push(' Lower '); 
          max = Math.round(max/2);
          document.getElementById("return").innerHTML = guess;
          guess = Math.round(guess-max);
          results.push(guess);  
          steps = steps+1;
      }
      else if(guess < answer)
      {
          results.push(' Higher ');
          max = Math.round(max/2);
          document.getElementById("return").innerHTML = guess;
          guess = Math.round(guess+max);
          results.push(guess); 
          steps = steps+1;
      }
  }   
    /// document.getElementById('guess').innerHTML = 'Is it ' + guess + '?'
    document.write(results);
}
</script>
</body>
</html>

