fun paranteze(x: String): Boolean {
    val stack: Stack<Char> = Stack()
    for (i in 0 until x.length) {
        if (x[i] == '(' || x[i] == '[' || x[i] == '{') {//verifica daca e una din cele 3 tipuri de paranteze atunci pune in  stiva
            stack.push(x[i])
        }
        if (stack.isEmpty())
            return false
        if(x[i]==')')//daca paranteza corespunde aceluiasi tip ca sa poata fi inchisa,atunci va fi scoasa din stiva
        {
            stack.pop()
            if(x[i]=='{' || x[i]=='[' )
                return false//altfel nu,adica  parantezele nu sunt inchise corect
        }
        if(x[i]=='}')
        {
            stack.pop()
            if(x[i]=='(' || x[i]=='[' )
                return false
        }
        if(x[i]==']')
        {
            stack.pop()
            if(x[i]=='(' || x[i]=='{')
                return false
        }
    }
    return stack.isEmpty()//stiva este goala
}

