/// Assumes that neither list is empty. 
void * zip(Node* f, Node* s){

    while(f != nullptr && s != nullptr){

        // Save next pointer.
        Node* n = f->next;

        // Point element from this list to other list.
        f->next = s;

        // Update first and second node.
        f = s;
        s = n;
    }
}

