const zipperLinkedListsIterative = (h1, h2) => {
    let cur1 = h1;
    let cur2 = h2;
    let next1;
    let next2;
    while(cur2 !== null && cur1 !== null){
        next1 = cur1.next;
        next2 = cur2.next;
        cur1.next = cur2;
        cur2.next = next1;
        cur2 = next2;
        cur1 = next1;
    }
    if (cur2 !== null) cur1.next = cur2;
    return h1;
}

const zipperLinkedListsRecursive = (h1, h2, cur1 = h1, cur2 = h2) => {
    if (!cur1) {
        cur1 = cur2;
        return h1;
    }
    if (!cur2) return h1;
    let next1 = cur1.next;
    let next2 = cur2.next;
    cur1.next = cur2;
    cur2.next = next1;
    return zipperLinkedListsRecursive(h1, h2, next1, next2);
}
