public static int FindinArray(int[] A, int x){
        if(A.length == 0 || x > A[A.length-1]){
            return -1;
        }
        if(A[A.length - 1] == x){
            return A.length - 1;
        }
        int mid = A.length /2;
        if(mid == 0){
            return -1;
        }

        if(A[mid -1] >= x){
            return FindinArray(Arrays.copyOfRange(A,0, mid), x);
        }
        else{
            return mid + FindinArray(Arrays.copyOfRange(A,mid, A.length-1), x);
        }
    }

    public static void main(String[] args){
        int y = FindinArray(new int[]{-1,0,4,5,6,8,11,12,15}, 15);
