        if l[z]<Min:
            Min=l[z]
            Pos=z
    return Min,Pos

def swap(l,Min,i,Pos):
    l[Pos]=l[i]
    l[i]=Min
    return l

def Sort(l):
    for i in range(0,len(l)-1):
        Min,Pos=MinFunction(l,i)
        T=swap(l, Min, i, Pos)

