public static int multiplesOf3and5(int number) {
        int totalSum = 0;
        int currentNumber = 0;
        while (true) {
            currentNumber += 3;
            if (currentNumber >= number) break;
            else totalSum += currentNumber;
        }
        currentNumber = 0;
        while (true) {
            currentNumber += 5;
            if (currentNumber >= number) return totalSum;
            else {
                if (currentNumber % 3 == 0) continue;
                else totalSum += currentNumber;
            }
        }
    }
