import Navbar from "./Navbar";

// example code from this course 36:59
const page = (
    <div>
        <h1>This is some heading </h1>
        <p>Paragraph</p>
    </div>
)

const root = ReactDOM.createRoot(document.getElementById('root'))
root.render(

        <div>
            <Navbar />
            { page }
        </div> 
)
Hope it can help to new learners. Happy Coding 😘

