class WidgetsExemple(GridLayout):
    text = StringProperty("Hello!")
    number = 0

    def on_button_click(self):
        self.number += 1

        if self.number > 0:
            cont = self.number
            if cont > 0:
                self.text = str(cont)
                cont += 1

