import { collection, query, orderBy, onSnapshot, doc, addDoc, updateDoc, deleteDoc } from "firebase/firestore";

    addNewQweet() {
      let newQweet = {
        content: this.newQweetContent,
        date: Date.now(),
        liked: false,
      };
      addDoc(collection(db, "qweets"), newQweet);
      this.newQweetContent = "";
    },
    toggleLiked(qweet) {
      updateDoc(doc(db, "qweets", qweet.id), {
        liked: !qweet.liked,
      });
    },
    deleteQweet(qweet) {
      deleteDoc(doc(db, "qweets", qweet.id));
    },

