// class.js


'use strict';


export default {}


export class Animal {
 constructor(type) {
  this.type = type
 }
 makeSound() {
  let sound
  switch(this.type) {
   case 'cat':
    sound = 'meow'
    break
   case 'dog':
    sound = 'woof'
    break
   case 'cow':
    sound = 'moo'
    break
   default:
    sound = 'none'
  }
  return sound
 }
 static About() {
  console.log('This simple program is about animals')
 }


}
