bool canSumMemo(vector <int> numList, int sum, vector<int> &memoTable)
{
	if (sum == 0) return true;
	if (memoTable[sum] != -1) return memoTable[sum];

	for (int i : numList)
	{
		if (sum-i >=0 && canSumMemo(numList, sum - i, memoTable))
			return memoTable[sum] = true;
	}

	return memoTable[sum] = false;
}

bool canSum(vector<int> numList, int target)
{
	vector<int> memoTable(target + 1, -1);
	return canSumMemo(numList, target, memoTable);
}

