const gridTraveler = (m, n) => {
  if(m<0 || n<0) return -1;
  let mem_number = (m<n ? n : m) - 1; 
  let mem_lower_val = factorial(mem_number);
  mem_number = (m<n ? m : n) - 1;
  let mem_value = factorial( mem_number, (m<n ? n : m) -1, mem_lower_val);
  return factorial(m+n-2, mem_number, mem_value) / mem_value / mem_lower_val;
};
