}

JavaScript code (slightly less readable IMHO)

function fib2(n) { // return the fib(n-1) and fib(n)
  if (n <= 2) return [1,1];
  var fibs = fib2(n-1);
  return [fibs[1], fibs[0] + fibs[1]];
}

function fib(n) {
  var fibs = fib2(n);
  return fibs[1];
}

