function NumOfPaths(_w, _h){
	var numerator = _w - 1;
	var denominator = 2;
	var result = _w;
	if (_h == 1){
		result = 1;
	}

	for(var i = 2; i < _h; i++){
		result = Math.round(result * (1 + (numerator/denominator)));
		denominator++;
	}

	trace("for " + _w + "x" + _h + " grid: " + result + " paths.");
