bool canSum(int target, vector<int> &nums, unordered_map<int, bool> &memo)
{
    if (target == 0) { return memo[target]; }
    if (target < 0 || memo.count(target)) { return false; }

    for (auto num : nums) {
        if (canSum(target - num, nums, memo)) {
            memo[target] = true;
            return true;
        }
    }

    memo[target] = false;
    return false;
}

