unsigned int sum(int target, vector<int> &nums, vector<int> &res, unordered_map<int,bool> mem)
{
    if (target == 0) {
        return true; 
    }
    if (target < 0) {
        return false;
    }
    if (mem.count(target)) {
        return mem[target];
    }

    for (auto const &num : nums) {
        if (sum(target-num, nums, res, mem)) {
            mem[target] = true;
            res.push_back(num);
            return true;
        }
    }

    mem[target] = false;    
    return false;
};

