def gridTraveler(m, n):
    grid = [[0 for i in range(m+1)] for i in range(n + 1)]
    grid[1][1] = 1
    for i in range(1, len(grid)):
        for j in range(1, len(grid)):
            if i == 1 and j == 1:
                pass
            else:
                grid[i][j] = grid[i][j-1] + grid[i-1][j]
    return grid[m][n]
