// double memo
const gridTraveler2 = (n, m, memo = {}) => {
    const key = `${n},${m}`;
    const reverseKey = `${m},${n}`;
    if (n === 0 || m === 0) return 0;
    if (n === 1 && m === 1) return 1;
    if (key in memo) {
        return memo[key];
    } else if (reverseKey in memo) {
        return memo[reverseKey];
    }
    const down = gridTraveler2(n - 1, m, memo);
    const right = gridTraveler2(n, m - 1, memo);
    memo[key] = down + right;
    memo[reverseKey] = down + right;
    return memo[key];
}

def best_sum(target_sum, array, memo={}):
    if target_sum in memo:
        return memo[target_sum]
    if target_sum==0:
        return []
    if target_sum <0:
        return None
    shortest_path=None
    for ele in array:
        diff = target_sum - ele
        result=best_sum(diff,array,memo)
        if result!=None:
            result.append(ele)
            if shortest_path==None or len(result)<len(shortest_path):
                shortest_path=result

    memo[target_sum]=shortest_path
    return shortest_path

