public class Test {

    private static int fib(int n) {
        int[] table = new int[n + 2]; // We need two extra elements in the first and the last positions of the array.
        table[1] = 1;
        for (int i = 0; i < n; i++) {
            table[i + 1] += table[i];
            table[i + 2] += table[i];
        }
        return table[n];
    }

