def get_options_memo(target: int, arr: list, memo=dict) -> list:
    if target in memo:
        return memo[target]
    elif target == 0:
        return []
    elif target < 0:
        return None

    for i in arr:
        target_r = target - i

        option = get_options_memo(target_r, arr, memo)
        if option != None:
            option.append(i)
            memo[target] = option
            return memo[target]

    memo[target] = None
    return None

