public static boolean canSumMemo(int target, int[] array, Map<Integer, Boolean> memo)
	{
		if(memo.containsKey(target))
		{
			return memo.get(target);
		}
		
		if (target == 0) return true;
		if (target < 0) return false;
		
		for (int num : array)
